import React,{useState, useEffect,useRef,memo} from "react";
import Select from 'react-select';
import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin, { Draggable } from '@fullcalendar/interaction';

import '../styles/App.css';

const ExternalDrag = memo(({event}) =>{
  let elemRef = useRef(null);
  useEffect(()=>{
    let draggable = new Draggable(elemRef.current, {
      eventData: function (){
        return{ ...event, create: true};
      }
    });
    return() => draggable.destroy()
  })
  return(
    <div 
      ref = {elemRef}
      className="fc-event" 
      title={event.title}
      >
      <div>
      {event.title}

      </div>

    </div>
  )


});
function App() {
  // const events = [{title: "today's event", date: new Date()}];
  // let array  =[
  //     {title:"event 1", id:"1"},
  //     {title:"event 2", id: "2"}
  //   ]
  const [recepies,setRecepies] = useState([
    {title:"event 1", id:"1"},
    {title:"event 2", id: "2"}
  ]);


   window.onload=function(){



    // selecting the elements for which we want to add a tooltip
    const target = document.getElementById("draggable");
    const tooltip = document.getElementById("tooltip-text");

    // change display to 'block' on mouseover
    target.addEventListener('mouseover', () => {
      tooltip.style.display = 'block';
    }, false);

    // change display to 'none' on mouseleave
    target.addEventListener('mouseleave', () => {
      tooltip.style.display = 'none';
    }, false);

    }

    // document.querySelector("").addEventListener("click")







  return (
    <div className="App">

      <h1 >
       Plate it!
      </h1>


      <div class="hide">I am shown when someone hovers over the div above.</div>

      <div class="tooltip-container">
  <p id="tooltip-text">The tooltip text{1}.</p>
</div>

      <div>
        <div id = "draggable">
          {recepies.map((event) => (
            <ExternalDrag key ={event.id} event = {event}/>

          ))}
        </div>



        <FullCalendar 
          headerToolbar={{
            left: "",
            center: "title",
            right: "timeGridWeek,timeGridDay"
          }}
          selectable

          plugins={[timeGridPlugin,interactionPlugin]} 

          eventClick ={
            function(arg){
            // alert(arg.event.title)
            alert(arg.event.start)
            }

          }





          />
      </div>
    </div>
  );
}

export default App;
